import React, { useState } from 'react'
import axios from "axios"

const backend_url = "http://localhost:3010"


const styles = {
  chatContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "50px"
  },
  messageContainer: {
    width: "50%",
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start",
    marginTop: "20px",
    padding: "20px",
    borderRadius: "10px",
    backgroundColor: "#f2f2f2"
  },
  userMessage: {
    alignSelf: "flex-start",
    backgroundColor: "#e6e6e6",
    padding: "10px",
    borderRadius: "10px",
    marginBottom: "10px"
  },
  responseMessage: {
    alignSelf: "flex-end",
    backgroundColor: "#ffffff",
    padding: "10px",
    borderRadius: "10px",
    marginBottom: "10px"
  },
  inputContainer: {
    width: "50%",
    display: "flex",
    alignItems: "center",
    marginTop: "20px"
  },
  inputBox: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    borderRadius: "10px",
    border: "none"
  },
  submitButton: {
    padding: "10px",
    marginLeft: "10px",
    fontSize: "16px",
    backgroundColor: "#4CAF50",
    color: "#ffffff",
    borderRadius: "10px",
    border: "none"
  }
};

function ChatApp() {
  const [query, setQuery] = useState("");
  const [texts, setTexts] = useState([]);

  const pairs = texts.map(([q, r]) => "User:" + q + "\nJOY:" + r + "\n\n");
  const input = pairs.reduce((acc, val) => acc + val, "");

  return (
    <div style={styles.chatContainer}>
        <br />
        <center>
            <h2> What's on your mind? </h2>
        </center>
        <div style={styles.inputContainer}>
            <input
            style={styles.inputBox}
            type="text"
            value={query}
            onChange={e => {
                setQuery(e.target.value);
            }}
        />
        <input
          style={styles.submitButton}
          type="button"
          value="Submit"
          onClick={async e => {
            e.preventDefault();

            const input_fr = input + "\n\nUser:" + query + "\nJOY:";
            console.log("Sending query", input_fr);
            const response = await axios.get(backend_url + "/webhook", {params: {input: input_fr}})

            console.log(response)
            setTexts([...texts, [query, response.data]])

        }}/>
        <br/>
        <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', width: '50%', margin: 'auto'}}>
            {texts.map(([q,r]) => (
                <div style={{display: 'flex', flexDirection: 'row', margin: '10px 0'}}>
                    <div style={{backgroundColor: 'lightblue', padding: '10px', borderRadius: '10px', marginRight: '10px'}}>
                        <p>{q}</p>
                    </div>
                    <div style={{backgroundColor: 'lightgray', padding: '10px', borderRadius: '10px'}}>
                        <p>{r}</p>
                    </div>
                </div>
            ))}
        </div>
    </div>
    </div>
)
}


export default ChatApp
