import React, { useState } from 'react'
import { Component } from 'react'
import MediaDisplay from "./MediaDisplay.js"
import axios from "axios"
import rest from './rest.png';
const backend_url = "http://localhost:3010"




function VideoCall() {
    const [query, setQuery] = useState("");
    const [texts, setTexts] = useState([]);

    const pairs = texts.map(([q,r]) => "User:" + q + "\nJOY:" + r + "\n\n")
    const input = pairs.reduce((acc, val) => acc + val, "")

    const media = {
        type: 'image',
        src: 'rest.png',
        alt: 'Example Image'
      };

    <img src={"./rest.png"} />
    return (
        <div>
            <h1> Let's chat </h1>
            <label>
                Message:
                <input type="text" value={query} onChange={(e)=>{setQuery(e.target.value) }}/>

            </label>
            <input type="button" value="Submit" onClick={async (e)=>{
                    e.preventDefault()

                const input_fr = input + "\n\nUser:"+query + "\nJOY:"
                // console.log("Sending query", input_fr)
                const response = await axios.get(backend_url + "/talking", {params: {input: input_fr}})

                console.log(response)
                setTexts([...texts, [query, response.data]])

            }}/>  
            <img
        src="https://bobbyhadz.com/images/blog/react-prevent-multiple-button-clicks/thumbnail.webp"
        alt="car"
      />
            {/* <MediaDisplay type="image" src="./../rest.jpg" alt="Image" /> */}
            {/* <MediaDisplay type="video" src="./videos/video.mp4" /> */}
        </div> 
        )




}

export default VideoCall
