import React, { useState } from 'react'
import { Component } from 'react'
import axios from "axios" 
const backend_url = "http://localhost:3010"

const VideoPlayer = ({videosrc}) => {
    return (
      <div>
        <video width="320" height="240" controls>
          <source src="./output_new.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    );
  };

function VideoCall() {
    const [query, setQuery] = useState("");
    const [texts, setTexts] = useState([]);

    const pairs = texts.map(([q,r]) => "User:" + q + "\nJOY:" + r + "\n\n")
    const input = pairs.reduce((acc, val) => acc + val, "")

     return (
        <div>
            <h1> Let's chat! </h1>
            <video width="320" height="240" controls>
                <source src="/Users/sophieliu/Desktop/Piano Translator copy/aicontent/src/components/rest.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <br/>
            <label>
                Message:
                <input type="text" value={query} onChange={(e)=>{setQuery(e.target.value) }}/>

            </label> 

            <input type="button" value="Submit" onClick={async (e)=>{
                    e.preventDefault()

                const input_fr = input + "\n\nUser:"+query + "\nJOY:"
                console.log("Sending query", input_fr)
                const response = await axios.get(backend_url + "/talking", {params: {input: input_fr}})

                console.log(response)
                setTexts([...texts, [query, response.data]])

            }}/>


        </div>

        ) 
} 
export default VideoCall
